function Checky(){var a={ch:[]};if(!localStorage.checks){localStorage.checks=JSON.stringify(a);}a=JSON.parse(localStorage.checks);a.ch.forEach(function(b){document.getElementById(b.id).checked=b.status;});this.checkyfy=function(b){document.getElementById(b).onclick=function(f){var c=false;for(var d=0;d<a.ch.length;d++){if(a.ch[d].id===b){a.ch[d].status=document.getElementById(b).checked;localStorage.checks=JSON.stringify(a);c=true;break;}}if(!c){obj={id:b,status:document.getElementById(b).checked};a.ch.push(obj);localStorage.checks=JSON.stringify(a);}};};}